import {BiArrowToTop} from "react-icons/bi";
import {useState,useEffect} from "react";

export default () => 
{

    const ICON_SIZE = 20;
    const topElement = document.getElementsByClassName("sliderbar")[0];
    const [active,setActive] = useState(false); 

    // Scroll listener
    function handleScroll()
    {
        let isActive = true;
        if( topElement.offsetTop < window.pageYOffset + 200)
            isActive = !isActive;   

        setActive(isActive);
    }

     // Add and remove scroll listeners 
     useEffect(() => {
        window.addEventListener('scroll',handleScroll);
        return () => 
        {
            window.removeEventListener('scroll',handleScroll)
        };
    });

    function clickHandler()
    {
         // Scroll to the clicked category
         topElement.scrollIntoView({ behavior: 'smooth' });
    }


    return(
        <button className="upbutton" onClick={clickHandler}>
            <BiArrowToTop className="icon" size={ICON_SIZE}/>
        </button>
            );
}
