import {useState,useEffect} from 'react'




function Navbar()
{
    const [position,setPosition] = useState('navbar');
    const [items,setItems] = useState([
        {name:'ABOUT',position:0,active:true},
        {name:'THINGS TO DO',position:0,active:false},
        {name:'GET INSPIRED',position:0,active:false},
        {name:'GALLERY',position:0,active:false},
        {name:'INFO',position:0,active:false},
    ]);

    // Scroll listener
    function handleScroll()
    {
        if(window.pageYOffset >= 30)
        {
            setPosition('navbar fixed');
        }
        else
        {
            setPosition('navbar');
        }
    }

    // Add and remove scroll listeners R
    useEffect(() => {
        window.addEventListener('scroll',handleScroll);
        return () => 
        {
            window.removeEventListener('scroll',handleScroll)
        };
    });



    function handleItemClick(event)
    {
        console.log(event.target);
    }


    return(
    <div className={position}>
        <div className='logo'>FAST TRAVEL</div>
        <div className="nav-items">
            {items.map((item) =>
            {
                const class_name = item.active ? 'nav-item active':'nav-item';
                return <a className={class_name} onClick={handleItemClick}>{item.name}</a>
            })}
        </div>
    </div>
    );
}

export default Navbar;
