export default () => 
{

    const ICON_SIZE = 20;
    const TOP_CLASS_NAME = "sliderbar";


     // Add and remove scroll listeners 
     useEffect(() => {
        window.addEventListener('scroll',handleScroll);
        return () => 
        {
            window.removeEventListener('scroll',handleScroll)
        };
    });

    function clickHandler()
    {
         // Scroll to the clicked category
         document.getElementsByClassName(TOP_CLASS_NAME)[0].scrollIntoView({ behavior: 'smooth' });
    }


    return(
        <button className="upbutton" onClick={clickHandler}>
            <BiArrowToTop className="icon" size={ICON_SIZE}/>
        </button>
            );
}
